{"windows":"[
  {
    "key": "ctrl+alt+l",
    "command": "turboConsoleLog.displayLogMessage"
  },
  {
    "key": "ctrl+l",
    "command": "expandLineSelection"
  },
  {
    "key": "ctrl+alt+d",
    "command": "editor.action.duplicateSelection"
  },

  {
    "key": "ctrl+shift+w",
    "command": "editor.emmet.action.wrapWithAbbreviation"
  },
  {
    "key": "ctrl+tab",
    "command": "workbench.action.openPreviousEditorFromHistory"
  },
  {
    "key": "tab",
    "command": "selectNextSuggestion",
    "when": "suggestWidgetMultipleSuggestions && suggestWidgetVisible && textInputFocus"
  },
  {
    "key": "ctrl+tab",
    "command": "selectPrevSuggestion",
    "when": "suggestWidgetMultipleSuggestions && suggestWidgetVisible && textInputFocus"
  },
  {
    "key": "ctrl+tab",
    "command": "workbench.action.quickOpenNavigateNext",
    "when": "inQuickOpen"
  },

  {
    "key": "ctrl+shift+o",
    "command": "workbench.action.files.openFile",
    "when": "true"
  },

  {
    "key": "ctrl+o",
    "command": "workbench.action.gotoSymbol"
  },
  {
    "key": "ctrl+n",
    "command": "extension.advancedNewFile"
  },

  {
    "key": "ctrl+space e",
    "command": "workbench.view.explorer",
    "when": "viewContainer.workbench.view.explorer.enabled"
  },

  {
    "key": "ctrl+space f",
    "command": "workbench.action.findInFiles"
  },
  {
    "key": "ctrl+space j",
    "command": "editor.action.copyLinesDownAction"
  },
  {
    "key": "ctrl+space k",
    "command": "editor.action.copyLinesDownAction"
  },
  //File Explorer utils
  {
    "command": "workbench.files.action.focusOpenEditorsView",
    "key": "e",
    "when": "filesExplorerFocus && !inputFocus"
  },
  {
    "command": "renameFile",
    "key": "r",
    "when": "filesExplorerFocus && !inputFocus"
  },
  //Git source control utils
  {
    "key": "d",
    "command": "git.clean",
    "when": "sideBarFocus && !inputFocus && activeViewlet == 'workbench.view.scm'"
  },
  //Terminal navigation
  {
    "key": "ctrl+shift+c",
    "command": "workbench.action.terminal.newWithCwd",
    "args": {
      "cwd": "${fileDirname}"
    }
  },

  {
    "key": "ctrl+space x",
    "command": "workbench.action.focusActiveEditorGroup",
    "when": "terminalFocus || filesExplorerFocus"
  },
  {
    "key": "ctrl+shift+a",
    "command": "workbench.action.terminal.focusNext",
    "when": "terminalFocus"
  },
  {
    "key": "ctrl+shift+b",
    "command": "workbench.action.terminal.focusPrevious",
    "when": "terminalFocus"
  },
  {
    "key": "ctrl+shift+n",
    "command": "workbench.action.terminal.new",
    "when": "terminalFocus"
  },
  {
    "key": "ctrl+shift+t",
    "command": "workbench.action.terminal.toggleTerminal",
    "when": "terminal.active"
  },
  {
    "key": "ctrl+shift+q",
    "command": "workbench.action.terminal.killActiveTab",
    "when": "terminalFocus"
  },

  {
    "key": "ctrl+shift+c",
    "command": "workbench.action.terminal.clear",
    "when": "terminalFocus"
  },
  //remove default keybindings
  {
    "key": "ctrl+shift+n",
    "command": "-workbench.action.newWindow"
  },
  {
    "key": "down",
    "command": "-selectNextSuggestion",
    "when": "suggestWidgetMultipleSuggestions && suggestWidgetVisible && textInputFocus"
  },
  {
    "key": "up",
    "command": "-selectPrevSuggestion",
    "when": "suggestWidgetMultipleSuggestions && suggestWidgetVisible && textInputFocus"
  }
]","linux":"[
  // Sidebar controls
  {
    "key": ",",
    "command": "workbench.action.toggleSidebarVisibility",
    "when": "filesExplorerFocus && !inputFocus"
  },
  {
    "key": "space g",
    "command": "workbench.action.toggleSidebarVisibility",
    "when": "sideBarFocus && activeViewlet == 'workbench.view.scm'"
  },
  {
    "key": "a",
    "command": "explorer.newFile",
    "when": "explorerViewletVisible && filesExplorerFocus && !explorerResourceIsRoot && !explorerResourceReadonly && !inputFocus"
  },
  {
    "key": "r",
    "command": "renameFile",
    "when": "explorerViewletVisible && filesExplorerFocus && !explorerResourceIsRoot && !explorerResourceReadonly && !inputFocus"
  },
  {
    "key": "x",
    "command": "filesExplorer.cut",
    "when": "explorerViewletVisible && filesExplorerFocus && !explorerResourceIsRoot && !explorerResourceReadonly && !inputFocus"
  },
  {
    "key": "y",
    "command": "filesExplorer.copy",
    "when": "explorerViewletVisible && filesExplorerFocus && !explorerResourceIsRoot && !inputFocus"
  },
  {
    "key": "p",
    "command": "filesExplorer.paste",
    "when": "explorerViewletVisible && filesExplorerFocus && !explorerResourceReadonly && !inputFocus"
  },

  {
    "key": "ctrl+j",
    "command": "search.action.focusSearchList",
    "when": "inputFocus && searchViewletFocus"
  },
  {
    "key": "q",
    "command": "workbench.action.toggleSidebarVisibility",
    "when": "sideBarVisible && !inputFocus"
  },
  // Navigating menus
  {
    "key": "ctrl+n",
    "command": "workbench.action.quickOpenSelectNext",
    "when": "inQuickOpen"
  },
  {
    "key": "ctrl+p",
    "command": "workbench.action.quickOpenSelectPrevious",
    "when": "inQuickOpen"
  },
  {
    "key": "ctrl+p",
    "when": "codeActionMenuVisible",
    "command": "selectPrevCodeAction"
  },
  {
    "key": "ctrl+n",
    "when": "codeActionMenuVisible",
    "command": "selectNextCodeAction"
  },
  {
    "key": "h",
    "command": "editor.action.scrollLeftHover",
    "when": "editorHoverFocused"
  },
  {
    "key": "j",
    "command": "editor.action.scrollDownHover",
    "when": "editorHoverFocused"
  },
  {
    "key": "k",
    "command": "editor.action.scrollUpHover",
    "when": "editorHoverFocused"
  },
  {
    "key": "l",
    "command": "editor.action.scrollRightHover",
    "when": "editorHoverFocused"
  },
  //Terminal controls
  {
    "key": "ctrl+tab",
    "command": "workbench.action.terminal.focusNext",
    "when": "terminalFocus && terminalHasBeenCreated && !terminalEditorFocus || terminalFocus && terminalProcessSupported && !terminalEditorFocus"
  },
  {
    "key": "f1",
    "command": "workbench.action.terminal.new",
    "when": "terminalProcessSupported || terminalWebExtensionContributedProfile"
  },
  {
    "key": "f2",
    "command": "workbench.action.terminal.kill",
    "when": "terminalFocus"
  },
  {
    "key": "ctrl+k",
    "command": "workbench.action.toggleMaximizedPanel",
    "when": "terminalFocus"
  },
  {
    "key": "ctrl+f",
    "command": "",
    "when": "terminalFocus"
  },
  {
    "key": "ctrl+p",
    "command": "",
    "when": "terminalFocus"
  },

  // Quickfix list
  {
    "key": "space q",
    "command": "workbench.actions.view.problems",
    "when": "focusedView == 'workbench.panel.markers.view'"
  },
  {
    "key": "o",
    "command": "workbench.actions.view.problems",
    "when": "focusedView == 'workbench.panel.markers.view'"
  },
  {
    "key": "ctrl+k",
    "command": "editor.action.inlineSuggest.acceptNextLine",
    "when": "inlineSuggestionVisible && !editorReadonly"
  }
]"}
